package org.sansilab.leetcode.problem;

import java.util.*;

// https://leetcode.com/problems/substring-with-concatenation-of-all-words/
public class SubstringWithConcatenationOfAllWords {
	public static void main(String[] args){
		SubstringWithConcatenationOfAllWords sln=new SubstringWithConcatenationOfAllWords();
//		String str="wordgoodgoodgoodbestword";
//		String[] words = {"word","good","best","good"};

		String str="";
		String[] words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
		long s=System.currentTimeMillis();
		Object output=sln.findSubstring(str, words);
		long e=System.currentTimeMillis();
		System.out.println(e-s+" ms");
		System.out.println(output.toString());
	}

	public List<Integer> findSubstring(String s, String[] words) {
		List<Integer> ret = new ArrayList<Integer>();
		if (words.length==0) {
			for (int i = 0; i < s.length(); i++) {
				ret.add(i);
			}
			return ret;
		}


		int wlen = words[0].length();
		int tlen = words.length*wlen;
		if (tlen>s.length()) {
			return ret;
		}
		Map<Integer, String> map = new HashMap<Integer, String>(words.length);
		Map<String, Integer> cmap = new HashMap<String, Integer>(words.length);
		for (String w:words) {
			putw(cmap, w);
		}
		for (Map.Entry<String, Integer> ent:cmap.entrySet()) {
			String w=ent.getKey();
			int from = 0;
			while (from < s.length() - w.length()+1) {
				int i = s.indexOf(w, from);
				if (i>-1){
					map.put(i, w);
					from = i+1;
				} else {
					break;
				}
			}
		}
		for (Map.Entry<Integer, String> ent:map.entrySet()) {
			Map<String, Integer> set = new HashMap<String, Integer>(words.length);
			int si = ent.getKey();
			if (si>s.length()-tlen) {
				continue;
			}
			set.put(ent.getValue(), 1);

			boolean f=true;
			for (int i = 1; i < words.length; i++) {
				String w = map.get(si+i*wlen);
				if (w==null || runout(set, cmap, w)) {
					f=false;
					break;
				}
				putw(set, w);
			}
			if (f) {
				ret.add(si);
			}
		}
		return ret;
	}

	private void putw(Map<String, Integer> map, String w){
		Integer c = map.get(w);
		if (c==null) {
			map.put(w, 1);
		} else {
			map.put(w, c+1);
		}
	}

	private boolean runout(Map<String, Integer> set, Map<String, Integer> cmap, String w){
		Integer c = set.get(w);
		if (c==null) {
			return false;
		}
		return c>=cmap.get(w);
	}
}

